def get_p_q(phi, N):
    res = []
    for i in range(len(phi)):
        b = int(N-phi[i]+1)
        coeff = [1, b, N]
        res.append(np.roots(coeff))
    return res


def Wiener(e,N):
    cur = e/N
    e_N = []
    e_N.append(floor(cur))
    i = 0
    con = 1
    while con != 0:
        cur = 1/cur
        con = round(cur,6)
        
        new = int(round(cur,6))
        e_N.append(new)
        cur = cur - new
        con = con - new

    cur = e/N
    k_d = []
    k_d.append(e_N[0])
    for item in range(1, len(e_N)):
        den = 0
        for j in reversed(range(1, item+1)):
            den += e_N[j]
            den = 1/den
        k_d.append(e_N[0]+den)
    

    frac = [Fraction(f).limit_denominator() for f in k_d[1:]]
    phi = []
    for  i in range(len(frac)):
        cur = (e*frac[i].denominator - 1)/frac[i].numerator
        if cur - round(cur, 6) == 0:
            phi.append(cur)
    phi = [int(i) for i in phi]
    res = get_p_q(phi, N)

    return res

